@keyframes surprise-pop{

    0% {
        transform: translateY(24px) scale(.6) rotate(-2deg);
        opacity: 0;
    }

    16% {
        transform: translateY(-8px) scale(1.15) rotate(0);
        opacity: 1;
    }

    22% {
        transform: translateY(0) scale(1.00);
    }

    25% {
        transform: translateY(-3px) scale(1.03);
    }

    28% {
        transform: translateY(0) scale(1.00);
    }

    100% {
        transform: translateY(0) scale(1.00);
        opacity: 1;
    }

}

